2265c2c4f10d71f7.factor_expression:
regime = TS_MEAN(($high - $low) / ($close + 1e-8) + ($close - $low) / ($high - $low + 1e-8) + TS_STD($return, 5), 5);
pv_divergence = TS_MEAN(($close - TS_MEAN($close, 10)) / (TS_STD($close, 10) + 1e-8) * ($volume - TS_MEAN($volume, 10)) / (TS_STD($volume, 10) + 1e-8), 10);
intraday_momentum = TS_MEAN($close - TS_MEAN($close, 3), 3);
combined_signal = pv_divergence + intraday_momentum;
regime_scaled_signal = combined_signal * (1 + regime);

e0677b0b90b77d7a.factor_expression:
// Volatility regime composite (as per hypothesis: 5-day composite of three indicators)
IntradayVol = ($high - $low) / ($close + 1e-8);
LowerShadow = ($close - $low) / ($high - $low + 1e-8);
PureVol = TS_STD($return, 5);
RegimeComposite = TS_MEAN(IntradayVol + LowerShadow + PureVol, 5);

// Regime classification (high/low) and scaling
RegimeSignal = IF(RegimeComposite > TS_MEAN(RegimeComposite, 20), 1.5, 0.8);

// Intraday momentum (3-day normalized by volatility)
Momentum = TS_MEAN(($close - $open) / ($high - $low + 1e-8), 3);

// Price-volume divergence (10-day)
PriceVolumeDiv = TS_MEAN(($close - TS_MEAN($close, 2)) * ($volume - TS_MEAN($volume, 2)) / (TS_STD($close, 5) * TS_STD($volume, 5) + 1e-8), 10);

// Combined factor with regime conditioning
Factor = RegimeSignal * (0.6 * Momentum + 0.4 * PriceVolumeDiv);

580cb19986b336fd.factor_expression:
// To match the hypothesis and description more accurately:
intraday_vol = TS_STD(($high - $low) / ($close + 1e-8), 10)
lower_shadow_strength = TS_MEAN(MAX(($close - $low) / ($high - $low + 1e-8), 0), 10)
price_volatility = TS_STD($return, 10)

// Regime transition probability (simplified as weighted combination of changes)
regime_prob = (DELTA(intraday_vol, 5) + DELTA(lower_shadow_strength, 5) + DELTA(price_volatility, 5)) / 3

// Stress vulnerability: volatility asymmetry-liquidity interaction
asymmetry = MAX(-SIGN($return) * TS_STD($return, 5), 0)
liquidity_change = TS_STD(DELTA($volume, 1) / ($volume + 1e-8), 5)
stress_vuln = TS_CORR(asymmetry, liquidity_change, 40)

// Dynamic weighting based on market volatility
market_vol = TS_STD($return, 20)
market_vol_weight = market_vol / TS_MEAN(market_vol, 60)

// Composite factor with multiplicative interaction
Composite_Transition_Stress_15D = regime_prob * stress_vuln * market_vol_weight
